function hsl(hue) {
  return `hsl(${hue}deg, 88%, 88%, 88%)`;
}

function randomHue() {
  let output = [];
  // let mainColor = Math.random() * 360;
  let mainColor = 337 + Math.random() * 47;
  if (mainColor >= 360) mainColor = mainColor - 360;

  let zeroColor = mainColor - 120;
  if (zeroColor < 0) zeroColor = zeroColor + 360;
  let secondColor = mainColor + 120;
  if (secondColor > 360) secondColor = secondColor - 360;
  return (output = [zeroColor, mainColor, secondColor]);
}

function timing(i) {
  return 333 * Math.pow(i + 1, 1 / 3);
}

class Layer {
  constructor() {
    this.zeroX = currentX;
    this.zeroY = currentY;

    this.x0 = 0;
    this.y0 = 0;
    this.x1 = 0;
    this.y1 = 0;
    this.x2 = 0;
    this.y2 = 0;

    this.opacity = 100;

    this.removeStart = false;
    this.shifted = false;

    //Создание трех векторов с рандомным цветом.
    this.colors = randomHue();
    for (let color of this.colors) {
      this[color] = document.createElement("div");
      this[color].className = "goddess h" + Math.floor(color);
      this[color].innerHTML = createVector(color);
      container.appendChild(this[color]);
    }
  }
  move() {
    // Управление положением слоя.
    this.deltaX = this.zeroX - currentX;
    this.deltaY = this.zeroY - currentY;
    this.sumDelta = Math.sqrt(this.deltaX ** 2 + this.deltaY ** 2);
    this.atan = Math.atan2(this.deltaY, this.deltaX);

    if (this.sumDelta < maxDelta * 3) {
      this.cosAlpha0 = Math.cos(this.atan + Math.PI * 2);
      this.sinAlpha0 = Math.sin(this.atan + Math.PI * 2);

      this.cosAlpha1 = Math.cos(this.atan + (Math.PI * 4) / 3);
      this.sinAlpha1 = Math.sin(this.atan + (Math.PI * 4) / 3);

      this.cosAlpha2 = Math.cos(this.atan + (Math.PI * 2) / 3);
      this.sinAlpha2 = Math.sin(this.atan + (Math.PI * 2) / 3);

      this.x0 = this.sumDelta * this.cosAlpha0;
      this.y0 = this.sumDelta * this.sinAlpha0;

      this.x1 = this.sumDelta * this.cosAlpha1;
      this.y1 = this.sumDelta * this.sinAlpha1;

      this.x2 = this.sumDelta * this.cosAlpha2;
      this.y2 = this.sumDelta * this.sinAlpha2;

      this.setMargin();
    }

    // Удалине слоя при превышении дельты.
    if (this.sumDelta >= maxDelta / 8) {
      this.delete();
    }

    //Удаление слоя при бездействии.
    this.inactiveDelete = setTimeout(() => {
      if (
        !this.removeStart &&
        this.sumDelta != 0 &&
        this.sumDelta >= maxDelta
      ) {
        this.delete();
      }
    }, 333);
  }
  setMargin() {
    this.layers = [0, 1, 2];
    for (let layer of this.layers) {
      if (this["x" + layer] >= 0)
        this[this.colors[layer]].style.marginRight = -this["x" + layer] + "px";
      else this[this.colors[layer]].style.marginLeft = this["x" + layer] + "px";
      if (this["y" + layer] >= 0)
        this[this.colors[layer]].style.marginTop = -this["y" + layer] + "px";
      else
        this[this.colors[layer]].style.marginBottom = this["y" + layer] + "px";
    }
  }
  delete() {
    clearTimeout(this.inactiveDelete);
    if (!this.removeStart && layers.length <= 2) {
      createNewLayer();
      this.removeStart = true;
      for (let color of this.colors) {
        let opacityAnimation = setInterval(() => {
          this[color].style.opacity = this.opacity + "%";
          this.opacity--;
          if (this.opacity <= 0) {
            clearInterval(opacityAnimation);
            container.removeChild(this[color]);
            if (!this.shifted) {
              this.shifted = !this.shifted;
              layers.shift();
            }
          }
        }, 1);
      }
    }
  }
}

function createNewLayer() {
  layers.push(new Layer());
}

function setZeroPoints(event) {
  if (event.gamma || event.beta) {
    currentX = Math.abs(event.gamma);
    currentY = Math.abs(event.beta);
    window.removeEventListener("deviceorientation", setZeroPoints);
  } else {
    currentX = event.clientX;
    currentY = event.clientY;
    window.addEventListener("mousemove", setZeroPoints);
  }
}

function handleOrientation(event) {
  if (event.gamma || event.beta) {
    currentX = event.gamma;
    currentY = event.beta - 90;
  } else {
    currentX = event.clientX / mouseSensivity;
    currentY = event.clientY / mouseSensivity;
  }

  for (let layer of layers) {
    layer.move();
  }
}

